mapper.update:
update AssetPlanInfo set APStatus=#{param.APStatus}, APStage=#{param.APStage}, ReviewResult=#{param.ReviewResult},
ModifiTime=SYSDATE() where DeleteFlag=1 and AssetPlanID in
#{code}

where.if:
and ApplyUser = #{param.ApplyUser}
or Reviewer = #{param.ApplyUser}
or Dept3Manager = #{param.ApplyUser}
or Dept2Manager = #{param.ApplyUser}
or Planner = #{param.ApplyUser}
or OQDeptReviewer = #{param.ApplyUser}
or Dept1Reviewer = #{param.ApplyUser}

mapper.update:
UPDATE AssetPlanInfo a LEFT JOIN RateTotalInfo b ON a.AssetPlanID=b.id
SET a.UsageRate=b.rateDetail WHERE a.AssetPlanID IN

	#{item}

mapper.select:
select  from AssetPlanInfo 

	DeleteFlag=1  and OutTimePlanEnum !=0

		and ReviewResult = #{param.ReviewResult}


		and APStage = #{param.APStage}



		and FIND_IN_SET(#{param.Reviewer}, ReviewPerson)


		and Dept3Manager = #{param.Dept3Manager}


		and Dept2Manager = #{param.Dept2Manager}


		and Planner = #{param.Planner}


		and OQDeptReviewer = #{param.OQDeptReviewer}


		and Dept1Reviewer = #{param.Dept1Reviewer}


		and substring_index(PlanCode, '-', 2) = #{param.ApplyMonth}

mapper.update:
update  AssetPlanInfo set  

		${param.column} =case  Reviewer when #{param.value} then #{param.value} else    concat(#{param.value},',',Reviewer) end 



		${param.column} = #{param.value}

	,Modifier=#{param.modifier},ModifiTime=NOW() 

	DeleteFlag=1  and OutTimePlanEnum !=0

		and ReviewResult = #{param.ReviewResult}


		and APStage = #{param.APStage}



		and FIND_IN_SET(#{param.Reviewer}, ReviewPerson)


		and Dept3Manager = #{param.Dept3Manager}


		and Dept2Manager = #{param.Dept2Manager}


		and Planner = #{param.Planner}


		and OQDeptReviewer = #{param.OQDeptReviewer}


		and Dept1Reviewer = #{param.Dept1Reviewer}


		and substring_index(PlanCode, '-', 2) = #{param.ApplyMonth}

mapper.select:
SELECT distinct a.APStatus,a.apstage,CONCAT(b.emp_name,' ',b.emp_code) as Reviewer FROM ( SELECT '规范性审核'APStatus , apstage,case when instr( ReviewPerson,',')>0 then REPLACE(REPLACE(ReviewPerson,Reviewer,''),',','') ELSE ReviewPerson END AS Reviewer FROM AssetPlanInfo WHERE apstage=2 AND DeleteFlag=1 AND OutTimePlanEnum=-1 UNION ALL SELECT '三级部门主管审核' APStatus ,apstage,Dept3Manager AS Reviewer FROM AssetPlanInfo WHERE apstage=3 AND DeleteFlag=1 AND OutTimePlanEnum=-1 UNION ALL SELECT '二级部门主管审核' APStatus ,apstage,Dept2Manager AS Reviewer FROM AssetPlanInfo WHERE apstage=4 AND DeleteFlag=1 AND OutTimePlanEnum=-1 UNION ALL SELECT '计划员审核' APStatus ,apstage,Planner AS Reviewer FROM AssetPlanInfo WHERE apstage=5 AND DeleteFlag=1 AND OutTimePlanEnum=-1 UNION ALL SELECT '专家团审核' APStatus ,apstage,OQDeptReviewer AS Reviewer FROM AssetPlanInfo WHERE apstage=6 AND DeleteFlag=1 AND OutTimePlanEnum=-1 UNION ALL SELECT '一级部门主管审核' APStatus ,apstage,Dept1Reviewer AS Reviewer FROM AssetPlanInfo WHERE apstage=7 AND DeleteFlag=1 AND OutTimePlanEnum=-1) a LEFT JOIN UserInfo b ON a.Reviewer=b.emp_code
a.Reviewer =#{userCode}
order by a.apstage
